import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
let EditPostComponent = class EditPostComponent {
    constructor(userService, rou) {
        this.userService = userService;
        this.rou = rou;
    }
    ngOnInit() {
        this.editPostForm = new FormGroup({
            Blog: new FormControl('', [Validators.required]),
            Picture: new FormControl('', [Validators.required]),
        });
    }
    upload(event) {
        this.file = event.target.files[0];
    }
    ;
    async updatepost() {
        if (this.editPostForm.value) {
            let post_data = new FormData();
            post_data.append("Blog", this.editPostForm.value.Blog);
            post_data.append("testimage", this.file);
            const data = await this.userService.editPost(post_data);
        }
    }
};
